const UnderlineFieldInParagraph = ({
  value = '',
  width = 120,
}: {
  value?: string;
  width?: number;
}) => {
  const trimmed = value.trim();

  // Approx: Each underscore ≈ 6px width → width / 6 = total underscores
  const underlineLength = Math.floor(width / 6);
  const generatedUnderline = '_'.repeat(underlineLength);

  return (
    <Text
      style={{
        fontFamily: 'Times-Roman',
        textDecoration: 'underline',
        paddingBottom: 3,
      }}
    >
      {trimmed || generatedUnderline}
    </Text>
  );
};

const FullUnderlineFieldFixed = ({
  value = '',
  width = 120,
}: {
  value?: string;
  width?: number;
}) => {
  const trimmed = value.trim();

  // Each space ≈ 5px in Times-Roman → width / 5 = total spaces
  const spaceCount = Math.floor(width / 5);
  const underlineSpaces = '_'.repeat(spaceCount);

  return (
    <Text
      style={{
        fontFamily: 'Times-Roman',
        textDecoration: 'underline',
        display: 'inline-block', // ⬅️ IMPORTANT: Keeps underline on same line!
        width, // ⬅️ Underline matches width exactly
        marginTop: 2,
      }}
    >
      {trimmed || underlineSpaces}
    </Text>
  );
};

// UNDERLINE FIELD — ALWAYS SHOWS UNDERLINE (even when empty)
const UnderlineField = ({ value = '', width = 300 }: { value?: string; width?: number }) => {
  const displayValue = value.trim() || ' ';
  return (
    <Text
      style={{
        fontFamily: 'Times-Roman',
        borderBottom: '1pt solid #000',
        display: 'inline-block',
        width,
        minHeight: 16,
        paddingBottom: 2,
      }}
    >
      {displayValue}
    </Text>
  );
};

