export const generatePDF = async (data: SafetyPlanFormData) => {
  const blob = await pdf(<SafetyPlanPDF data={data} />).toBlob();
  const base64WithPrefix = await new Promise<string>(resolve => {
    const reader = new FileReader();
    reader.onload = () => resolve(reader.result as string);
    reader.readAsDataURL(blob);
  });

  return base64WithPrefix.split(',')[1]; // PURE BASE64 STRING
};

export default SafetyPlanPDF;
